let count = 0;

const user = "XXXXxxxxXXXX";

const lang = "chinese";

const mode = "random"; // message or random

const rand = "emoji"; // emoji or language

const len = 25; // how many random chars

const message = "yeet";

const interval = 1 * 1000; // 3 secconds

setInterval(function(){ 
	axios.post("http://onyolo.com/"+user+"/message", {
		text: (mode == "message") ? message : (rand == "emoji") ? randomEmojis(len) : randomLanguage(lang, len), 
		cookie: Math.random().toString(36).substring(2, 15) + Math.random().toString(36).substring(2, 15), 
		wording: ""})
	.then(function (response) {
		count++;
		console.log(count);
	});
}, interval);
